// Mock appointment data - Replace with actual API calls
const mockAppointments = [
    { id: 1, patientName: "Jane Doe", date: "2024-03-20", time: "10:00", status: "Scheduled" },
    { id: 2, patientName: "John Smith", date: "2024-03-21", time: "11:30", status: "Scheduled" },
];

const mockDoctors = {
    Dentist: [
        { id: 1, name: 'Dr. John Smith', specialization: 'Dentist', experience: '10 years', image: 'https://source.unsplash.com/random/300x200/?dentist' },
        { id: 2, name: 'Dr. Sarah Johnson', specialization: 'Dentist', experience: '8 years', image: 'https://source.unsplash.com/random/300x200/?dentist' },
    ],
    Cardiology: [
        { id: 3, name: 'Dr. Michael Brown', specialization: 'Cardiology', experience: '15 years', image: 'https://source.unsplash.com/random/300x200/?cardiologist' },
        { id: 4, name: 'Dr. Emily Davis', specialization: 'Cardiology', experience: '12 years', image: 'https://source.unsplash.com/random/300x200/?cardiologist' },
    ],
    // ... other specializations
};

export const getSpecializations = () => {
    return Object.keys(mockDoctors);
};

export const getDoctorsBySpecialization = (specialization) => {
    return mockDoctors[specialization] || [];
};

export const getAppointments = () => {
    return mockAppointments;
};

export const bookAppointment = async (appointmentData) => {
    // Mock booking - Replace with actual API call
    const newAppointment = {
        id: mockAppointments.length + 1,
        ...appointmentData,
        status: "Scheduled"
    };
    mockAppointments.push(newAppointment);
    return newAppointment;
};

export const getDoctorSchedule = (doctorId) => {
