import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  Card,
  CardContent,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Avatar,
  Chip,
  Divider,
} from '@mui/material';
import {
  CalendarToday,
  AccessTime,
  Person,
  Event,
} from '@mui/icons-material';
import { getAppointments, getPatientDetails } from '../services/appointmentService';
import PatientDetails from '../components/PatientDetails';

const DoctorDashboard = () => {
  const [appointments, setAppointments] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [patientDetailsOpen, setPatientDetailsOpen] = useState(false);
  const [todayAppointments, setTodayAppointments] = useState([]);
  const [upcomingAppointments, setUpcomingAppointments] = useState([]);

  useEffect(() => {
    const loadAppointments = () => {
      const allAppointments = getAppointments();
      setAppointments(allAppointments);

      // Filter today's appointments
      const today = new Date().toISOString().split('T')[0];
      const todayAppts = allAppointments.filter(apt => apt.date === today);
      setTodayAppointments(todayAppts);

      // Filter upcoming appointments (future dates)
      const upcoming = allAppointments.filter(apt => apt.date > today);
      setUpcomingAppointments(upcoming);
    };

    loadAppointments();
  }, []);

  const handlePatientClick = (patientId) => {
    const details = getPatientDetails(patientId);
    setSelectedPatient(details);
    setPatientDetailsOpen(true);
  };

  const handleSaveNotes = (notes) => {
    if (selectedPatient) {
      // Update patient notes logic here
      setSelectedPatient({ ...selectedPatient, doctorNotes: notes });
    }
  };

  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case 'scheduled':
        return 'primary';
      case 'completed':
        return 'success';
      case 'cancelled':
        return 'error';
      default:
        return 'default';
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      {/* Summary Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ bgcolor: 'primary.light', color: 'white' }}>
            <CardContent>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Typography variant="h6">Today's Appointments</Typography>
                <CalendarToday />
              </Box>
              <Typography variant="h3" sx={{ mt: 2 }}>
                {todayAppointments.length}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ bgcolor: 'success.light', color: 'white' }}>
            <CardContent>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Typography variant="h6">Upcoming</Typography>
                <Event />
              </Box>
              <Typography variant="h3" sx={{ mt: 2 }}>
                {upcomingAppointments.length}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ bgcolor: 'warning.light', color: 'white' }}>
            <CardContent>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Typography variant="h6">Total Patients</Typography>
                <Person />
              </Box>
              <Typography variant="h3" sx={{ mt: 2 }}>
                {appointments.length}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ bgcolor: 'info.light', color: 'white' }}>
            <CardContent>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Typography variant="h6">Hours Today</Typography>
                <AccessTime />
              </Box>
              <Typography variant="h3" sx={{ mt: 2 }}>
                8
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Today's Appointments */}
      <Paper sx={{ p: 3, mb: 4 }} elevation={3}>
        <Typography variant="h5" gutterBottom sx={{ mb: 3 }}>
          Today's Appointments
        </Typography>
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow sx={{ bgcolor: 'grey.100' }}>
                <TableCell>Time</TableCell>
                <TableCell>Patient</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {todayAppointments.map((appointment) => (
                <TableRow key={appointment.id} hover>
                  <TableCell>{appointment.time}</TableCell>
                  <TableCell>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                      <Avatar>{appointment.patientName[0]}</Avatar>
                      {appointment.patientName}
                    </Box>
                  </TableCell>
                  <TableCell>
                    <Chip
                      label={appointment.status}
                      color={getStatusColor(appointment.status)}
                      size="small"
                    />
                  </TableCell>
                  <TableCell>
                    <Button
                      variant="outlined"
                      size="small"
                      onClick={() => handlePatientClick(appointment.patientId)}
                    >
                      View Details
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>

      {/* Upcoming Appointments */}
      <Paper sx={{ p: 3 }} elevation={3}>
        <Typography variant="h5" gutterBottom sx={{ mb: 3 }}>
          Upcoming Appointments
        </Typography>
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow sx={{ bgcolor: 'grey.100' }}>
                <TableCell>Date</TableCell>
                <TableCell>Time</TableCell>
                <TableCell>Patient</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {upcomingAppointments.map((appointment) => (
                <TableRow key={appointment.id} hover>
                  <TableCell>{appointment.date}</TableCell>
                  <TableCell>{appointment.time}</TableCell>
                  <TableCell>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                      <Avatar>{appointment.patientName[0]}</Avatar>
                      {appointment.patientName}
                    </Box>
                  </TableCell>
                  <TableCell>
                    <Chip
                      label={appointment.status}
                      color={getStatusColor(appointment.status)}
                      size="small"
                    />
                  </TableCell>
                  <TableCell>
                    <Button
                      variant="outlined"
                      size="small"
                      onClick={() => handlePatientClick(appointment.patientId)}
                    >
                      View Details
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>

      {/* Patient Details Dialog */}
      <PatientDetails
        open={patientDetailsOpen}
        onClose={() => setPatientDetailsOpen(false)}
        patient={selectedPatient}
        isDoctor={true}
        onSaveNotes={handleSaveNotes}
      />
    </Container>
  );
};

export default DoctorDashboard;
