import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Box,
  AppBar,
  Toolbar,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import {
  AccountCircle,
  Info,
  ContactSupport,
  CalendarMonth,
} from '@mui/icons-material';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider, DateCalendar } from '@mui/x-date-pickers';
import { format, setHours, setMinutes } from 'date-fns';

const specializations = [
  'Dentist',
  'Cardiology',
  'Neurology',
  'Pediatrics',
  'Orthopedics',
  'Dermatology',
  'Ophthalmology',
  'ENT',
];

// Generate time slots from 9:00 to 19:00 with 30-minute intervals
const generateTimeSlots = () => {
  const slots = [];
  for (let hour = 9; hour < 19; hour++) {
    for (let minute = 0; minute < 60; minute += 30) {
      const time = format(setMinutes(setHours(new Date(), hour), minute), 'HH:mm');
      slots.push(time);
    }
  }
  return slots;
};

const timeSlots = generateTimeSlots();

// Sample doctor data
const doctors = {
  Dentist: [
    { id: 1, name: 'Dr. John Smith', specialization: 'Dentist', experience: '10 years', image: 'https://source.unsplash.com/random/300x200/?dentist' },
    { id: 2, name: 'Dr. Sarah Johnson', specialization: 'Dentist', experience: '8 years', image: 'https://source.unsplash.com/random/300x200/?dentist' },
  ],
  Cardiology: [
    { id: 3, name: 'Dr. Michael Brown', specialization: 'Cardiology', experience: '15 years', image: 'https://source.unsplash.com/random/300x200/?cardiologist' },
    { id: 4, name: 'Dr. Emily Davis', specialization: 'Cardiology', experience: '12 years', image: 'https://source.unsplash.com/random/300x200/?cardiologist' },
  ],
  Neurology: [
    { id: 5, name: 'Dr. Robert Wilson', specialization: 'Neurology', experience: '14 years', image: 'https://source.unsplash.com/random/300x200/?neurologist' },
    { id: 6, name: 'Dr. Lisa Anderson', specialization: 'Neurology', experience: '9 years', image: 'https://source.unsplash.com/random/300x200/?neurologist' },
  ],
  Pediatrics: [
    { id: 7, name: 'Dr. James Taylor', specialization: 'Pediatrics', experience: '11 years', image: 'https://source.unsplash.com/random/300x200/?pediatrician' },
    { id: 8, name: 'Dr. Maria Garcia', specialization: 'Pediatrics', experience: '7 years', image: 'https://source.unsplash.com/random/300x200/?pediatrician' },
  ],
  Orthopedics: [
    { id: 9, name: 'Dr. David Miller', specialization: 'Orthopedics', experience: '13 years', image: 'https://source.unsplash.com/random/300x200/?orthopedist' },
    { id: 10, name: 'Dr. Jennifer White', specialization: 'Orthopedics', experience: '10 years', image: 'https://source.unsplash.com/random/300x200/?orthopedist' },
  ],
  Dermatology: [
    { id: 11, name: 'Dr. Thomas Clark', specialization: 'Dermatology', experience: '12 years', image: 'https://source.unsplash.com/random/300x200/?dermatologist' },
    { id: 12, name: 'Dr. Patricia Lee', specialization: 'Dermatology', experience: '8 years', image: 'https://source.unsplash.com/random/300x200/?dermatologist' },
  ],
  Ophthalmology: [
    { id: 13, name: 'Dr. Richard Moore', specialization: 'Ophthalmology', experience: '15 years', image: 'https://source.unsplash.com/random/300x200/?ophthalmologist' },
    { id: 14, name: 'Dr. Susan Hall', specialization: 'Ophthalmology', experience: '11 years', image: 'https://source.unsplash.com/random/300x200/?ophthalmologist' },
  ],
  ENT: [
    { id: 15, name: 'Dr. Charles Young', specialization: 'ENT', experience: '14 years', image: 'https://source.unsplash.com/random/300x200/?ent' },
    { id: 16, name: 'Dr. Karen King', specialization: 'ENT', experience: '9 years', image: 'https://source.unsplash.com/random/300x200/?ent' },
  ],
};

const Appointment = () => {
  const navigate = useNavigate();
  const [selectedSpecialization, setSelectedSpecialization] = useState('');
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState('');
  const [openDialog, setOpenDialog] = useState(false);

  const handleSpecializationChange = (event) => {
    setSelectedSpecialization(event.target.value);
    setSelectedDoctor(null);
    setSelectedDate(null);
    setSelectedTime('');
  };

  const handleDoctorSelect = (doctor) => {
    setSelectedDoctor(doctor);
    setOpenDialog(true);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleTimeSelect = (time) => {
    setSelectedTime(time);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const handleBookAppointment = () => {
    // Here you would typically make an API call to book the appointment
    console.log('Booking appointment for:', {
      doctor: selectedDoctor,
      date: selectedDate,
      time: selectedTime,
    });
    setOpenDialog(false);
    // Show success message or redirect
  };

  return (
    <>
      <AppBar position="static" sx={{ backgroundColor: '#2196f3' }}>
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1, cursor: 'pointer' }} onClick={() => navigate('/')}>
            Spital Management
          </Typography>
          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button
              color="inherit"
              startIcon={<AccountCircle />}
              onClick={() => navigate('/register')}
            >
              Register
            </Button>
            <Button
              color="inherit"
              startIcon={<CalendarMonth />}
              onClick={() => navigate('/appointment')}
            >
              Appointment
            </Button>
            <Button
              color="inherit"
              startIcon={<Info />}
              onClick={() => navigate('/about')}
            >
              About Us
            </Button>
            <Button
              color="inherit"
              startIcon={<ContactSupport />}
              onClick={() => navigate('/contact')}
            >
              Contact Us
            </Button>
          </Box>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom align="center">
          Book an Appointment
        </Typography>
        
        <Box sx={{ mb: 4 }}>
          <FormControl fullWidth>
            <InputLabel id="specialization-label">Select Specialization</InputLabel>
            <Select
              labelId="specialization-label"
              value={selectedSpecialization}
              label="Select Specialization"
              onChange={handleSpecializationChange}
            >
              {specializations.map((specialization) => (
                <MenuItem key={specialization} value={specialization}>
                  {specialization}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Box>

        {selectedSpecialization && (
          <Grid container spacing={4}>
            {doctors[selectedSpecialization].map((doctor) => (
              <Grid item xs={12} sm={6} md={4} key={doctor.id}>
                <Card 
                  sx={{ 
                    height: '100%', 
                    display: 'flex', 
                    flexDirection: 'column',
                    cursor: 'pointer',
                    '&:hover': {
                      boxShadow: 6,
                    },
                  }}
                  onClick={() => handleDoctorSelect(doctor)}
                >
                  <CardMedia
                    component="img"
                    height="200"
                    image={doctor.image}
                    alt={doctor.name}
                  />
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography gutterBottom variant="h5" component="h2">
                      {doctor.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Specialization: {doctor.specialization}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Experience: {doctor.experience}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}

        {/* Appointment Booking Dialog */}
        <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
          <DialogTitle>
            Book Appointment with {selectedDoctor?.name}
          </DialogTitle>
          <DialogContent>
            <Grid container spacing={2}>
              <Grid item xs={12} md={6}>
                <LocalizationProvider dateAdapter={AdapterDateFns}>
                  <DateCalendar
                    value={selectedDate}
                    onChange={handleDateChange}
                    minDate={new Date()}
                  />
                </LocalizationProvider>
              </Grid>
              <Grid item xs={12} md={6}>
                <Typography variant="h6" gutterBottom>
                  Available Time Slots
                </Typography>
                <Grid container spacing={1}>
                  {timeSlots.map((time) => (
                    <Grid item xs={6} sm={4} key={time}>
                      <Button
                        variant={selectedTime === time ? "contained" : "outlined"}
                        fullWidth
                        onClick={() => handleTimeSelect(time)}
                      >
                        {time}
                      </Button>
                    </Grid>
                  ))}
                </Grid>
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDialog}>Cancel</Button>
            <Button 
              onClick={handleBookAppointment}
              variant="contained"
              disabled={!selectedDate || !selectedTime}
            >
              Book Appointment
            </Button>
          </DialogActions>
        </Dialog>
      </Container>
    </>
  );
};

export default Appointment;
