// Mock data for appointments
const mockAppointments = [
  {
    id: 1,
    patientId: 1,
    patientName: 'Jane Smith',
    date: new Date().toISOString().split('T')[0], // Today's date
    time: '10:00',
    status: 'Scheduled',
    patientDetails: {
      name: 'Jane Smith',
      age: 28,
      bloodType: 'A+',
      allergies: ['Penicillin', 'Pollen'],
      doctorNotes: 'Patient has a history of seasonal allergies. Regular check-ups recommended.'
    }
  },
  {
    id: 2,
    patientId: 2,
    patientName: 'John Wilson',
    date: new Date().toISOString().split('T')[0], // Today's date
    time: '11:30',
    status: 'Completed',
    patientDetails: {
      name: 'John Wilson',
      age: 45,
      bloodType: 'O+',
      allergies: ['Latex', 'Aspirin'],
      doctorNotes: 'Patient has hypertension. Blood pressure should be monitored regularly.'
    }
  },
  {
    id: 3,
    patientId: 3,
    patientName: 'Sarah Johnson',
    date: new Date().toISOString().split('T')[0], // Today's date
    time: '14:00',
    status: 'Scheduled',
    patientDetails: {
      name: 'Sarah Johnson',
      age: 35,
      bloodType: 'B+',
      allergies: ['Dairy'],
      doctorNotes: 'Patient is pregnant. Regular prenatal check-ups scheduled.'
    }
  },
  {
    id: 4,
    patientId: 4,
    patientName: 'Michael Brown',
    date: (() => {
      const tomorrow = new Date();
      tomorrow.setDate(tomorrow.getDate() + 1);
      return tomorrow.toISOString().split('T')[0];
    })(), // Tomorrow's date
    time: '09:30',
    status: 'Scheduled',
    patientDetails: {
      name: 'Michael Brown',
      age: 52,
      bloodType: 'AB-',
      allergies: ['Sulfa'],
      doctorNotes: 'Patient recovering from knee surgery. Physical therapy recommended.'
    }
  },
  {
    id: 5,
    patientId: 5,
    patientName: 'Emily Davis',
    date: (() => {
      const nextWeek = new Date();
      nextWeek.setDate(nextWeek.getDate() + 7);
      return nextWeek.toISOString().split('T')[0];
    })(), // Next week's date
    time: '15:30',
    status: 'Scheduled',
    patientDetails: {
      name: 'Emily Davis',
      age: 31,
      bloodType: 'A-',
      allergies: ['None'],
      doctorNotes: 'Annual check-up scheduled. Previous results all normal.'
    }
  }
];

// Mock data for doctors
const mockDoctors = {
  'Cardiology': [
    { id: 1, name: 'Dr. Sarah Johnson', specialization: 'Cardiology', experience: '15 years', image: 'https://example.com/doctor1.jpg' },
    { id: 2, name: 'Dr. Michael Chen', specialization: 'Cardiology', experience: '10 years', image: 'https://example.com/doctor2.jpg' }
  ],
  'Dentist': [
    { id: 3, name: 'Dr. Emily Brown', specialization: 'Dentist', experience: '8 years', image: 'https://example.com/doctor3.jpg' },
    { id: 4, name: 'Dr. David Wilson', specialization: 'Dentist', experience: '12 years', image: 'https://example.com/doctor4.jpg' }
  ]
};

export const getSpecializations = () => {
  return Object.keys(mockDoctors);
};

export const getDoctorsBySpecialization = (specialization) => {
  return mockDoctors[specialization] || [];
};

export const getAppointments = () => {
  return mockAppointments;
};

export const getPatientDetails = (patientId) => {
  const appointment = mockAppointments.find(app => app.patientId === patientId);
  return appointment ? appointment.patientDetails : null;
};

export const updatePatientNotes = (patientId, notes) => {
  const appointmentIndex = mockAppointments.findIndex(app => app.patientId === patientId);
  if (appointmentIndex !== -1) {
    mockAppointments[appointmentIndex].patientDetails.doctorNotes = notes;
    return true;
  }
  return false;
};

export const bookAppointment = (appointmentData) => {
  const newAppointment = {
    id: mockAppointments.length + 1,
    ...appointmentData,
    status: 'Scheduled',
    patientDetails: {
      name: appointmentData.patientName,
      age: 0,
      bloodType: 'Not specified',
      allergies: [],
      doctorNotes: ''
    }
  };
  mockAppointments.push(newAppointment);
  return newAppointment;
};

export const getDoctorSchedule = (doctorId) => {
  return {
    workingDays: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'],
    startTime: '09:00',
    endTime: '17:00',
    appointments: mockAppointments.filter(app => app.doctorId === doctorId)
  };
};
