import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Paper,
  Grid,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Tabs,
  Tab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  AppBar,
  Toolbar,
  Avatar,
  Chip,
} from '@mui/material';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { format } from 'date-fns';
import { Person, ExitToApp } from '@mui/icons-material';

// Temporary data - In a real app, this would come from your backend
const mockUser = {
  id: 1,
  name: "John Doe",
  email: "john@example.com",
  role: "admin", // Change this to test different roles: "admin", "doctor", "patient"
  phone: "+1234567890",
  address: "123 Main St",
  dateOfBirth: "1990-01-01"
};

const mockUsers = [
  { id: 1, name: "John Doe", email: "john@example.com", role: "admin" },
  { id: 2, name: "Dr. Smith", email: "smith@example.com", role: "doctor" },
  { id: 3, name: "Jane Patient", email: "jane@example.com", role: "patient" },
];

const mockAppointments = [
  { id: 1, patientName: "Jane Doe", date: "2024-03-20", time: "10:00", status: "Scheduled" },
  { id: 2, patientName: "John Smith", date: "2024-03-21", time: "11:30", status: "Scheduled" },
];

// Helper function to capitalize role
const capitalizeRole = (role) => {
  return role.charAt(0).toUpperCase() + role.slice(1);
};

// Header Component
const Header = ({ user }) => {
  const navigate = useNavigate();
  
  const getRoleColor = (role) => {
    switch (role) {
      case 'admin':
        return '#ff1744'; // red
      case 'doctor':
        return '#2979ff'; // blue
      case 'patient':
        return '#00c853'; // green
      case 'manager':
        return '#6200ea'; // purple
      default:
        return '#757575'; // grey
    }
  };

  return (
    <AppBar position="static" sx={{ backgroundColor: 'white', color: 'black', boxShadow: 1, mb: 3 }}>
      <Toolbar>
        <Typography variant="h6" sx={{ flexGrow: 1, cursor: 'pointer' }} onClick={() => navigate('/')}>
          My Account
        </Typography>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Chip
            label={capitalizeRole(user.role)}
            sx={{
              backgroundColor: getRoleColor(user.role),
              color: 'white',
              fontWeight: 'bold',
            }}
          />
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <Avatar sx={{ bgcolor: getRoleColor(user.role) }}>
              {user.name.charAt(0)}
            </Avatar>
            <Box sx={{ textAlign: 'right' }}>
              <Typography variant="subtitle2" sx={{ fontWeight: 'bold' }}>
                {user.name}
              </Typography>
              <Typography variant="caption" color="text.secondary">
                {user.email}
              </Typography>
            </Box>
          </Box>
          <Button
            variant="outlined"
            color="inherit"
            startIcon={<ExitToApp />}
            onClick={() => navigate('/login')}
            size="small"
          >
            Logout
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

// Patient View Component
const PatientView = ({ user }) => (
  <Paper sx={{ p: 3, mt: 3 }}>
    <Typography variant="h6" gutterBottom>Personal Information</Typography>
    <Grid container spacing={2}>
      <Grid item xs={12} sm={6}>
        <Typography><strong>Name:</strong> {user.name}</Typography>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Typography><strong>Email:</strong> {user.email}</Typography>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Typography><strong>Phone:</strong> {user.phone}</Typography>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Typography><strong>Address:</strong> {user.address}</Typography>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Typography><strong>Date of Birth:</strong> {user.dateOfBirth}</Typography>
      </Grid>
    </Grid>
  </Paper>
);

// Doctor View Component
const DoctorView = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());

  return (
    <Paper sx={{ p: 3, mt: 3 }}>
      <Box sx={{ mb: 3 }}>
        <Typography variant="h6" gutterBottom>Weekly Schedule</Typography>
        <DatePicker
          label="Week Starting"
          value={selectedDate}
          onChange={(newValue) => setSelectedDate(newValue)}
          sx={{ mb: 2 }}
        />
      </Box>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Patient Name</TableCell>
              <TableCell>Date</TableCell>
              <TableCell>Time</TableCell>
              <TableCell>Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {mockAppointments.map((appointment) => (
              <TableRow key={appointment.id}>
                <TableCell>{appointment.patientName}</TableCell>
                <TableCell>{appointment.date}</TableCell>
                <TableCell>{appointment.time}</TableCell>
                <TableCell>{appointment.status}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Paper>
  );
};

// Admin View Component
const AdminView = () => {
  const [openSchedule, setOpenSchedule] = useState(false);
  const [selectedDoctor, setSelectedDoctor] = useState('');

  const handleRoleChange = (userId, newRole) => {
    console.log(`Changing role for user ${userId} to ${newRole}`);
    // Implement role change logic here
  };

  const handleScheduleDoctor = () => {
    setOpenSchedule(true);
  };

  return (
    <>
      <Paper sx={{ p: 3, mt: 3 }}>
        <Typography variant="h6" gutterBottom>User Management</Typography>
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Name</TableCell>
                <TableCell>Email</TableCell>
                <TableCell>Role</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {mockUsers.map((user) => (
                <TableRow key={user.id}>
                  <TableCell>{user.name}</TableCell>
                  <TableCell>{user.email}</TableCell>
                  <TableCell>
                    <FormControl size="small" fullWidth>
                      <Select
                        value={user.role}
                        onChange={(e) => handleRoleChange(user.id, e.target.value)}
                      >
                        <MenuItem value="admin">Admin</MenuItem>
                        <MenuItem value="doctor">Doctor</MenuItem>
                        <MenuItem value="patient">Patient</MenuItem>
                        <MenuItem value="manager">Manager</MenuItem>
                      </Select>
                    </FormControl>
                  </TableCell>
                  <TableCell>
                    {user.role === 'doctor' && (
                      <Button
                        variant="contained"
                        size="small"
                        onClick={handleScheduleDoctor}
                      >
                        Set Schedule
                      </Button>
                    )}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>

      {/* Doctor Schedule Dialog */}
      <Dialog open={openSchedule} onClose={() => setOpenSchedule(false)} maxWidth="md" fullWidth>
        <DialogTitle>Set Doctor Schedule</DialogTitle>
        <DialogContent>
          <Box sx={{ mt: 2 }}>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <Typography variant="subtitle1" gutterBottom>
                  Set Working Hours
                </Typography>
              </Grid>
              <Grid item xs={6}>
                <TextField
                  label="Start Time"
                  type="time"
                  defaultValue="09:00"
                  InputLabelProps={{ shrink: true }}
                  fullWidth
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  label="End Time"
                  type="time"
                  defaultValue="17:00"
                  InputLabelProps={{ shrink: true }}
                  fullWidth
                />
              </Grid>
              <Grid item xs={12}>
                <Typography variant="subtitle1" gutterBottom sx={{ mt: 2 }}>
                  Working Days
                </Typography>
                {['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'].map((day) => (
                  <Button
                    key={day}
                    variant="outlined"
                    sx={{ m: 0.5 }}
                  >
                    {day}
                  </Button>
                ))}
              </Grid>
            </Grid>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenSchedule(false)}>Cancel</Button>
          <Button variant="contained" onClick={() => setOpenSchedule(false)}>
            Save Schedule
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
};

const MyAccount = () => {
  const navigate = useNavigate();

  // Render different views based on user role
  const renderContent = () => {
    switch (mockUser.role) {
      case 'patient':
        return <PatientView user={mockUser} />;
      case 'doctor':
        return <DoctorView />;
      case 'admin':
        return <AdminView />;
      default:
        return <Typography>Access Denied</Typography>;
    }
  };

  return (
    <>
      <Header user={mockUser} />
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        {renderContent()}
      </Container>
    </>
  );
};

export default MyAccount;
