import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  Alert,
  List,
  ListItem,
  ListItemText,
  Divider,
} from '@mui/material';
import { login } from '../services/authService';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const user = await login(email, password);
      console.log('Logged in user:', user);
      navigate('/myaccount');
    } catch (error) {
      setError(error.message);
    }
  };

  const testAccounts = [
    { email: 'john@example.com', role: 'Admin' },
    { email: 'smith@example.com', role: 'Doctor' },
    { email: 'jane@example.com', role: 'Patient' },
  ];

  return (
    <Container component="main" maxWidth="sm">
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Paper elevation={3} sx={{ p: 4, width: '100%' }}>
          <Typography component="h1" variant="h5" align="center" gutterBottom>
            Login
          </Typography>
          
          <Alert severity="info" sx={{ mb: 3 }}>
            This is a demo application. You can use any of the test accounts below to login.
            <br />
            <strong>Password for all accounts: 12345678</strong>
          </Alert>

          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}

          <form onSubmit={handleSubmit}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Sign In
            </Button>
          </form>

          <Divider sx={{ my: 3 }}>Test Accounts</Divider>
          
          <List>
            {testAccounts.map((account, index) => (
              <ListItem key={index} sx={{ pl: 0 }}>
                <ListItemText
                  primary={account.email}
                  secondary={`Role: ${account.role}`}
                />
                <Button
                  variant="outlined"
                  size="small"
                  onClick={() => setEmail(account.email)}
                >
                  Use This Account
                </Button>
              </ListItem>
            ))}
          </List>
        </Paper>
      </Box>
    </Container>
  );
};

export default Login;
