// Mock user data - Replace with actual API calls
const mockUsers = [
    { id: 1, name: "John Doe", email: "john@example.com", role: "admin", phone: "+1234567890", address: "123 Main St", dateOfBirth: "1990-01-01" },
    { id: 2, name: "Dr. Smith", email: "smith@example.com", role: "doctor" },
    { id: 3, name: "Jane Patient", email: "jane@example.com", role: "patient" },
];

export const login = async (email, password) => {
    // Mock login - Replace with actual API call
    const user = mockUsers.find(u => u.email === email);
    if (user) {
        localStorage.setItem('user', JSON.stringify(user));
        return user;
    }
    throw new Error('Invalid credentials');
};

export const logout = () => {
    localStorage.removeItem('user');
};

export const getCurrentUser = () => {
    const userStr = localStorage.getItem('user');
    return userStr ? JSON.parse(userStr) : null;
};

export const isAuthenticated = () => {
    return !!getCurrentUser();
};
